client_script:
frappe.web_form.on('flight', (field, value) => {
    // Ensure a flight is selected
    if (value) {
        // Fetch the corresponding Airplane Ticket where the flight is linked
        frappe.call({
            method: 'frappe.client.get',
            args: {
                doctype: 'Airplane Ticket',
                filters: { flight: value }, // Link field to match the flight
                fields: ['flight_price'] // The flight price you want to retrieve
            },
            callback: function(response) {
                if (response && response.message) {
                    // Set the Flight Price field to the fetched flight price value
                    let flight_price = response.message.flight_price;
                    frappe.web_form.set_value('flight_price', flight_price);
                }
            }
        });
    } else {
        // Clear the Flight Price field if no flight is selected
        frappe.web_form.set_value('flight_price', '');
    }
});
